// Custom JS
document.addEventListener('DOMContentLoaded', () => {

  // $('iframe svg path').css({'fill': 'var(--color-tit)'});

  // Аккордион ---------------------------------------------
  $(function () {
    var items = $(".accordion__item");

    items.on("click", function () {
      if ($(this).hasClass("active")) {
      } else {
        $(this).siblings().removeClass("active");
        $(this).toggleClass("active");
      }
    });
  });
  //--------------------------------------------------------------------------
  //POPUP open/close
	$(".open-popup, .specialist__btn, consultation-btn, .biography-btn").on("click", function (e) {
		e.preventDefault();
		var dataPopup = $('.' + $(this).attr("data-popup"));
		$(".popup-overlay").removeClass("open");
		$(dataPopup).addClass("open");
		$("body").addClass("locked");
	});
	// Скрытие popup по клику за его пределами 
	$(document).mouseup(function (e) {
		var popup = $(".popup");
		if (!popup.is(e.target) && popup.has(e.target).length === 0) {
			$("body").removeClass("locked");
			$(".popup-overlay").removeClass("open");
		}
	});
  $(".close-popup").on("click", function (e) {
		e.preventDefault();
		$(".popup-overlay").removeClass("open");
		$("body").removeClass("locked");
	});
  // SWIPER
  var swiper = new Swiper(".mySwiper", {
    effect: "creative",
    creativeEffect: {
      prev: {
        shadow: true,
        translate: [0, 0, -400],
      },
      next: {
        translate: ["100%", 0, 0],
      },
    },
    autoplay: {
      delay: 3000,
      disableOnInteraction: false,
    },
    rewind: true,
    lazy: true,
    autoHeight: true,
    centeredSlides: true,
    grabCursor: true,
    loop: true,
    pagination: {
      el: ".swiper-pagination",
      clickable: true,
    },
  });

  // =======
})

